create table ds_sys_sensitive_shuffle_info
(
    id                         bigint auto_increment comment '主键id字段'
        primary key,
    schema_name                varchar(50)                            not null,
    table_name                 varchar(100)                           not null comment '表名',
    incr_field_name            varchar(100)                           null comment '表增量字段，默认为无，全量刷表数据；若有，则指定最多一个增量字段，进行增量刷库；若一个表定义多个增量字段则取只其中一个',
    on_update_timestamp_fields varchar(225) default ''                null comment '表onUpdate时间戳的列表，多个用英文逗号隔开',
    create_time                timestamp    default CURRENT_TIMESTAMP null comment '创建时间',
    update_time                timestamp                              null on update CURRENT_TIMESTAMP comment '更新数据'
)
    comment '数据安全系统敏感-洗数信息';

